import React from 'react'
import styled from 'styled-components'

const Wrapper = styled.div`
  width: 100vw;
  height: 100vh;
  background: #424242;
  display: flex;
  flex-direction: column;
  justify-content: center;
  font-size: 16px;
  color: #fff;
  font-family: Arial, sans-serif;

  & > * {
    padding: 10px;
    border: 3px solid #fff;
    border-radius: 3px;
  }
`

const Status = styled.div`
    color: ${p =>
      p.status === 'disconnected' ? 'red' : p.status === 'connecting' ? 'orange' : 'green'}
    font-weight: 600;
    font-size: 18px;
    position: relative;
    bottom: 10px;
    left: 10px;
`

const Messages = styled.div`
  flex-grow: 1;
`

const Input = styled.input`
  background-color: #424242;
  color: #fff;
  width: 100%;
  height: 40px;
  box-sizing: border-box;
`

class Chat extends React.Component {
  state = {
    status: 'connecting',
    messages: [],
    message: '',
    userName: '',
    userColor: '',
  }

  handleInputChange = e => {
    this.setState({ message: e.target.value })
  }

  handleSubmit = () => {}

  render() {
    const { userName, userColor, message, messages, status } = this.state
    return (
      <Wrapper>
        <Messages>
          <Status status={status}>{status}</Status>
        </Messages>
        <Input type="text" value={message} onChange={e => this.handleInputChange(e)} />
      </Wrapper>
    )
  }
}

export default Chat
