import React from 'react'
import styled from 'styled-components'

const Wrapper = styled.div`
  width: 100vw;
  height: 100vh;
  background: #424242;
  display: flex;
  flex-direction: column;
  justify-content: center;
  font-size: 16px;
  color: #fff;
  font-family: Arial, sans-serif;

  & > * {
    padding: 10px;
    border: 3px solid #fff;
    border-radius: 3px;
  }
`

const Status = styled.div`
    color: ${p =>
      p.status === 'disconnected' ? 'red' : p.status === 'connection ...' ? 'orange' : 'green'}
    font-weight: 600;
    font-size: 18px;
    position: absolute;
    bottom: 10px;
    left: 10px;
    display: flex;
`

const Name = styled.div`
    color: ${p => p.color}
    font-size: 18px;
    margin-left: 5px;
`

const Messages = styled.div`
  flex-grow: 1;
  position: relative;
  padding: 40px 0;
  overflow-y: scroll;
`

const Msg = styled.div`
  color: #fff;
  font-size: 16px;
  padding: 20px 0;
  border-bottom: 1px solid #cfcfcf;
`

const Input = styled.input`
  background-color: #424242;
  color: #fff;
  width: 100%;
  height: 40px;
  box-sizing: border-box;
`
class Chat extends React.Component {
  _connection = null
  _input = React.createRef()

  state = {
    status: '',
    error: '',
    chatHistory: [],
    input: '',
    name: null,
    color: '',
  }

  componentWillMount = () => {
    const { connection } = this.props
    this._connection = connection
    this._connection.onopen = this.handleConnectionOpen
    this._connection.onerror = this.handleConnectionError
    this._connection.onmessage = this.handleConnectionMessage
  }

  componentDidMount() {
    document.addEventListener('keydown', this.handleSubmit)
  }

  handleSubmit = e => {
    const v = this._input.current.value

    const { name } = this.state

    if (v !== '' && e.keyCode === 13) {
      this._connection.send(v)

      this.setState(prev => ({
        name: prev.name ? prev.name : v,
        input: '',
        status: 'connected',
      }))
    }
  }

  handleConnectionOpen = () => {
    this.setState({ status: 'connection ...' })
  }

  handleConnectionError = err => {
    console.log('connection error', err)
    this.setState({
      status: 'disconnected',
      error: 'Connection failed',
    })
  }

  handleConnectionMessage = msg => {
    let { chatHistory } = this.state

    try {
      var json = JSON.parse(msg.data)
    } catch (e) {
      console.log('Invalid JSON: ', msg.data)
      return
    }

    if (json.type === 'message') {
      chatHistory.push(json.data)
      this.setState({ chatHistory })
    } else {
      this.setState(prev => ({
        ...prev,
        [json.type]: json.data,
      }))
    }
  }

  handleInputChange = e => {
    this.setState({ input: e.target.value })
  }

  render() {
    const { name, chatHistory, color, input, status, error } = this.state
    return (
      <Wrapper>
        <Messages>
          <Status status={status}>
            {status}
            {name && <Name color={color}>{`as : ${name}`}</Name>}
            {error !== '' && ` : ${error}`}
          </Status>
          {chatHistory.map(m => (
            <Msg>{m.text}</Msg>
          ))}
        </Messages>
        <Input
          ref={this._input}
          type="text"
          value={input}
          onChange={e => this.handleInputChange(e)}
          placeholder={name === '' ? 'Enter your name' : 'Your Message ...'}
        />
      </Wrapper>
    )
  }
}

export default Chat
