import React from 'react'
import styled from 'styled-components'

const Wrapper = styled.div`
  width: 100vw;
  height: 100vh;
  background: #424242;
  display: flex;
  justify-content: center;
  font-size: 16px;
  color: #fff;
  font-family: Arial, sans-serif;

  & > * {
    padding: 10px;
    border: 3px solid #fff;
    border-radius: 3px;
  }
`

const Messages = styled.div`
  flex-grow: 1;
`

const Input = styled.input`
  background-color: #424242;
  color: #fff;
  width: 100%;
  height: 150px;
`

class Chat extends React.Component {
  state = {
    status: 'Disconnected',
    messages: [],
    userName: '',
    userColor: '',
  }

  render() {
    return (
      <Wrapper>
        <div>Hey ! Welcome on this chat bro !</div>
      </Wrapper>
    )
  }
}

export default Chat
